import React, { Component } from 'react';
import { auth, createStuDocument } from '../config';

class Singup extends Component {
  state = { stuname: '', email: '', password: '', stunum: '' };

  createStuDocument = async (user, additionalData) => {
    if (!user) return;

    const userRef = firestore.doc(`students/${user.uid}`);

    const snapshot = await userRef.get();

    if (!snapshot.exists) {
      const { email } = user;
      const { stuname, stunum } = additionalData;

      try {
        await userRef.set({
          stuname,
          email,
          stunum
        });
      } catch (error) {
        console.log('Error in creating student', error);
      }
    }
  };

  handleChange = (e) => {
    const { name, value } = e.target;

    this.setState({ [name]: value });
  };

  handleSubmit = async (e) => {
    e.preventDefault();
    const { email, password, stuname, stunum } = this.state;
    try {
      console.log(stuname, stunum);
      const { user } = await auth.createUserWithEmailAndPassword(
        email,
        password
      );
      console.log(user,stunum);
      await createStuDocument(user, { stuname, stunum });
    } catch (error) {
      console.log('error', error);
    }

    this.setState({ stuname: '', email: '', password: '', stunum: '' });
  };

  render() {
    const { stuname, email, password, stunum } = this.state;
    return (
      <div>
        <form className="signup-login" onSubmit={this.handleSubmit}>
          <h2>Signup</h2>

          <input
            type="name"
            name="stuname"
            value={stuname}
            onChange={this.handleChange}
            placeholder="Name"
          />
          <input
            type="email"
            name="email"
            value={email}
            onChange={this.handleChange}
            placeholder="Email"
          />
          <input
            type="password"
            name="password"
            value={password}
            onChange={this.handleChange}
            placeholder="Password"
          />
          <input
            type="text"
            name="stunum"
            value={stunum}
            onChange={this.handleChange}
            placeholder="Name"
          />
          <button>Signup</button>
        </form>
      </div>
    );
  }
}

export default Singup;
