import React, { Component } from 'react';
import firebase from '../config';
import { Link } from 'react-router-dom';
import DashBoard from "./Dashboard";

class ShowStu extends Component {

  constructor(props) {
    super(props);
    this.state = {
      students: {},
      key: ''
    };
  }

  componentDidMount() {
    const ref = firebase.firestore().collection('students').doc(this.props.match.params.id);
    ref.get().then((doc) => {
      if (doc.exists) {
        this.setState({
          students: doc.data(),
          key: doc.id,
          isLoading: false
        });
      } else {
        console.log("No such document!");
      }
    });
  }

  delete(id){
    firebase.firestore().collection('students').doc(id).delete().then(() => {
      console.log("Document successfully deleted!");
      this.props.history.push("/")
    }).catch((error) => {
      console.error("Error removing document: ", error);
    });
  }

  render() {
    return (
      <>
      <header>
        <DashBoard />
      </header>
      <div class="container text-center">
        <div class="panel panel-default">
          <div class="panel-heading">
            <br /><br />
              <dt>Student ID</dt>
              {this.state.students.stunum}
          </div>
          <div class="panel-body">
            <dl>
            <dt>Student ID</dt>
              {this.state.students.stunum}
              <dt>Name:</dt>
              <dd>{this.state.students.stuname}</dd>
              <dt>class:</dt>
              <dd>{this.state.students.croom}</dd>
              <dt>Room:</dt>
              <dd>{this.state.students.nroom}</dd>
            </dl>
            <Link to={`/edit/${this.state.key}`} class="btn btn-success">Edit</Link>&nbsp;
            <button onClick={this.delete.bind(this, this.state.key)} class="btn btn-danger">Delete</button>
          </div>
        </div>
      </div>
      </>
    );
  }
}

export default ShowStu;
