import React, { Component } from "react";
import firebase from "../config";
import { Link } from "react-router-dom";

class Edit extends Component {
  constructor(props) {
    super(props);
    this.state = {
      key: "",
      stunum: "",
      stuname: "",
      croom: "",
      nroom: "",
      cid: "",
    };
  }

  componentDidMount() {
    const ref = firebase
      .firestore()
      .collection("students")
      .doc(this.props.match.params.id);
    ref.get().then((doc) => {
      if (doc.exists) {
        const students = doc.data();
        this.setState({
          key: doc.id,
          stunum: students.stunum,
          stuname: students.stuname,
          croom: students.croom,
          nroom: students.nroom,
          cid: students.cid,
        });
      } else {
        console.log("No such document!");
      }
    });
  }

  onChange = (e) => {
    const state = this.state;
    state[e.target.name] = e.target.value;
    this.setState({ students: state });
  };

  onSubmit = (e) => {
    e.preventDefault();

    const { stunum, stuname, croom, nroom, cid } = this.state;

    const updateRef = firebase
      .firestore()
      .collection("students")
      .doc(this.state.key);
    updateRef
      .set({
        stunum,
        stuname,
        croom,
        nroom,
        cid,
      })
      .then((docRef) => {
        this.setState({
          key: "",
          stunum: "",
          stuname: "",
          croom: "",
          nroom: "",
          cid: "",
        });
        this.props.history.push("/show/" + this.props.match.params.id);
      })
      .catch((error) => {
        console.error("Error adding document: ", error);
      });
  };

  render() {
    return (
      <div class="container">
        <div class="panel panel-default">
          <div class="panel-heading">
            <h3 class="panel-title">EDIT students</h3>
          </div>
          <div class="panel-body">
            <h4>
              <Link to={`/show/${this.state.key}`} class="btn btn-primary">
                students List
              </Link>
            </h4>
            <form onSubmit={this.onSubmit}>
              <div class="form-group">
                <label for="title">Student Id:</label>
                <input
                  type="text"
                  class="form-control"
                  name="stunum"
                  value={this.state.stunum}
                  onChange={this.onChange}
                  placeholder="ID"
                />
              </div>
              <div class="form-group">
                <label for="description">Student Name:</label>
                <input
                  type="text"
                  class="form-control"
                  name="stuname"
                  value={this.state.stuname}
                  onChange={this.onChange}
                  placeholder="Name"
                />
              </div>
              <div class="form-group">
                <label for="author">Class ID:</label>
                <input
                  type="text"
                  class="form-control"
                  name="cid"
                  value={this.state.cid}
                  onChange={this.onChange}
                  placeholder="Class ID"
                />
              </div>
              <div class="form-group">
                <label for="author">Class:</label>
                <input
                  type="text"
                  class="form-control"
                  name="croom"
                  value={this.state.croom}
                  onChange={this.onChange}
                  placeholder="Class"
                />
              </div>
              <div class="form-group">
                <label for="author">Room:</label>
                <input
                  type="text"
                  class="form-control"
                  name="croom"
                  value={this.state.croom}
                  onChange={this.onChange}
                  placeholder="Class"
                />
              </div>
              <button type="submit" class="btn btn-success">
                Submit
              </button>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default Edit;
