import React, { Component } from "react";
// import ReactDOM from 'react-dom';
import firebase from "../config";
// import { Link } from "react-router-dom";
import DashBoard from "./Dashboard";
import "./Create.css";

class CreateStu extends Component {
  constructor() {
    super();
    this.ref = firebase.firestore().collection("students");
    this.state = {
      stuid: "",
      stunum: "",
      stuname: "",
      croom: "",
      nroom: "",
      cid: "",
    };
  }
  onChange = (e) => {
    const state = this.state;
    state[e.target.name] = e.target.value;
    this.setState(state);
  };

  onSubmit = (e) => {
    e.preventDefault();

    const { stuid, stunum, stuname, croom, nroom, cid } = this.state;

    this.ref
      .add({
        stuid,
        stunum,
        stuname,
        croom,
        nroom,
        cid,
      })
      .then((docRef) => {
        this.setState({
          stuid: "",
          stunum: "",
          stuname: "",
          croom: "",
          nroom: "",
          cid: "",
        });
        this.props.history.push("/");
      })
      .catch((error) => {
        console.error("Error adding document: ", error);
      });
  };

  render() {
    const { stunum, stuname, croom, nroom, cid } = this.state;
    return (
      <>
        <header>
          <DashBoard />
        </header>
        <div className="container">
          <div className="panel panel-default">
            <div className="panel-body ">
              <br />
              <br />
              <h4 className="text-center">เพิ่มนักเรียนใหม่</h4>
              <br />
              <form onSubmit={this.onSubmit}>
                <div classNameName="form-group">
                  <label for="title">รหัสนักเรียน :</label>
                  <input
                    type="text"
                    className="form-control in fw-normal"
                    name="stunum"
                    value={stunum}
                    onChange={this.onChange}
                    placeholder="กรอกรหัสนักเรียน 6หลัก"
                  />
                </div>
                <div className="form-group">
                  <label for="title">ชื่อ - นามสกุล :</label>
                  <input
                    type="text"
                    className="form-control in"
                    name="stuname"
                    value={stuname}
                    onChange={this.onChange}
                    placeholder="กรอกชื่อ - นามสกุล"
                  />
                </div>
                <div className="form-group">
                  <label for="author">รหัสชั้นเรียน :</label>
                  <input
                    type="text"
                    className="form-control in"
                    name="cid"
                    value={cid}
                    onChange={this.onChange}
                    placeholder="Class"
                  />
                </div>
                <div className="form-group">
                  <label for="author">ชั้นปี :</label>
                  <input
                    type="text"
                    className="form-control in"
                    name="croom"
                    value={croom}
                    onChange={this.onChange}
                    placeholder="Class"
                  />
                </div>
                <div className="form-group">
                  <label for="title">ห้อง :</label>
                  <input
                    type="text"
                    className="form-control in"
                    name="nroom"
                    value={nroom}
                    onChange={this.onChange}
                    placeholder="Room"
                  />
                </div>
                <div className="text-center">
                  <br />
                  <button
                    type="submit"
                    className="btn btn-success text-center bt"
                  >
                    เพิ่ม
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </>
    );
  }
}

export default CreateStu;
