import React, { Component } from "react";
// import ReactDOM from 'react-dom';
import firebase from "../config";
// import { Link } from "react-router-dom";
import DashBoard from "./Dashboard";
import "./Create.css";

class CreateStu extends Component {
  constructor() {
    super();
    this.ref = firebase.firestore().collection("students");
    this.state = {
      stuid: "",
      stunum: "",
      stuname: "",
      croom: "",
      nroom: "",
    };
  }
  onChange = (e) => {
    const state = this.state;
    state[e.target.name] = e.target.value;
    this.setState(state);
  };

  onSubmit = (e) => {
    e.preventDefault();

    const { stuid, stunum, stuname, croom, nroom } = this.state;

    this.ref
      .add({
        stuid,
        stunum,
        stuname,
        croom,
        nroom,
      })
      .then((docRef) => {
        this.setState({
          stuid: "",
          stunum: "",
          stuname: "",
          croom: "",
          nroom: "",
        });
        this.props.history.push("/");
      })
      .catch((error) => {
        console.error("Error adding document: ", error);
      });
  };

  render() {
    const { stunum, stuname, croom, nroom } = this.state;
    return (
      <>
      <header>
        <DashBoard />
      </header>
      <div className="container">
        <div className="panel panel-default">
          <div className="panel-body ">
            <br /><br />
            <h4 className="text-center">
                Add Student
            </h4>
            <br />
            <form onSubmit={this.onSubmit}>
              <div classNameName="form-group">
                <label for="title">Student ID:</label>
                <input
                  type="text"
                  className="form-control"
                  name="stunum"
                  value={stunum}
                  onChange={this.onChange}
                  placeholder="Student Id"
                />
              </div>
              <div className="form-group">
                <label for="title">Student Name:</label>
                <input
                  type="text"
                  className="form-control in"
                  name="stuname"
                  value={stuname}
                  onChange={this.onChange}
                  placeholder="Student Name"
                />
              </div>
              <div className="form-group">
                <label for="author">Class:</label>
                <input
                  type="text"
                  className="form-control in"
                  name="croom"
                  value={croom}
                  onChange={this.onChange}
                  placeholder="Class"
                />
              </div>
              <div className="form-group">
                <label for="title">Room:</label>
                <input
                  type="text"
                  className="form-control in"
                  name="nroom"
                  value={nroom}
                  onChange={this.onChange}
                  placeholder="Room"
                />
              </div>
              <br />
              <button type="submit" className="btn btn-success">
                Add
              </button>
            </form>
          </div>
        </div>
      </div>
      </>
    );
  }
}

export default CreateStu;
