import React, { Component } from "react";
import firebase from "../config";
// import { Link } from "react-router-dom";
import "./Create.css";

class EditStu extends Component {
  constructor(props) {
    super(props);
    this.state = {
      key: "",
      email,
      stunum: "",
      stuname: "",
      croom: "",
      nroom: "",
      cid: "",
    };
  }

  componentDidMount() {
    const ref = firebase
      .firestore()
      .collection("students")
      .doc(this.props.match.params.id);
    ref.get().then((doc) => {
      if (doc.exists) {
        const students = doc.data();
        this.setState({
          key: doc.id,
          email: students.email,
          stunum: students.stunum,
          stuname: students.stuname,
          croom: students.croom,
          nroom: students.nroom,
          cid: students.cid,
        });
      } else {
        console.log("No such document!");
      }
    });
  }

  onChange = (e) => {
    const state = this.state;
    state[e.target.name] = e.target.value;
    this.setState({ students: state });
  };

  onSubmit = (e) => {
    e.preventDefault();

    const { email, stunum, stuname, croom, nroom, cid } = this.state;

    const updateRef = firebase
      .firestore()
      .collection("students")
      .doc(this.state.key);
    updateRef
      .set({
        email,
        stunum,
        stuname,
        croom,
        nroom,
        cid,
      })
      .then((docRef) => {
        this.setState({
          key: "",
          email: "",
          stunum: "",
          stuname: "",
          croom: "",
          nroom: "",
          cid: "",
        });
        this.props.history.push("/student");
      })
      .catch((error) => {
        console.error("Error adding document: ", error);
      });
  };

  render() {
    return (
      <div>
        <form className="signup-login" onSubmit={this.handleSubmit}>
          <h2 className="text-center">เพิ่มนักเรียนใหม่</h2>

          <input
            type="name"
            name="stuname"
            value={this.state.stuname}
            onChange={this.handleChange}
            placeholder="Name"
          />
          <input
            type="email"
            name="email"
            value={this.state.email}
            onChange={this.handleChange}
            placeholder="Email"
          />
          <input
            type="password"
            name="password"
            value={this.state.password}
            onChange={this.handleChange}
            placeholder="Password"
          />
          <input
            type="text"
            name="stunum"
            value={this.state.stunum}
            onChange={this.handleChange}
            placeholder="StudentID"
          />
          <input
            type="name"
            name="croom"
            value={this.state.croom}
            onChange={this.handleChange}
            placeholder="Class"
          />
          <input
            type="name"
            name="nroom"
            value={this.state.nroom}
            onChange={this.handleChange}
            placeholder="Room"
          />
          <button className="btsi">Signup</button>
        </form>
      </div>
    );
  }
}

export default EditStu;
