import firebase from 'firebase/app';
import 'firebase/auth';
import 'firebase/firestore';


const firebaseConfig = firebase.initializeApp({
    apiKey: "AIzaSyDfLVowRODd_-LtNrrEPEofm-2Gb2kf_F0",
    authDomain: "pj-time.firebaseapp.com",
    projectId: "pj-time",
    storageBucket: "pj-time.appspot.com",
    messagingSenderId: "548881226627",
    appId: "1:548881226627:web:a4e0e327cc4a3793eaf4b6",
    measurementId: "G-5K375YQ0DJ"
});

export const auth = firebase.auth();
export const firestore = firebase.firestore();

export const createstudentDocument = async (user, additionalData) => {
  if (!user) return;

  const userRef = firestore.doc(`users/${user.uid}`);

  const snapshot = await userRef.get();

  if (!snapshot.exists) {
    const { email } = user;
    const { displayName, stunum } = additionalData;

    try {
      await userRef.set({
        displayName,
        email,
        createdAt: new Date(),
        stunum
      });
    } catch (error) {
      console.log('Error in creating user', error);
    }
  }
};
export default firebaseConfig;
