import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import firebase from '../config';


class ViewStu extends Component {
    constructor(props) {
        super(props);
        this.ref = firebase.firestore().collection('students');
        this.unsubscribe = null;
        this.state = {
            user: []
        };
    }

    onCollectionUpdate = (querySnapshot) => {
        const user = [];
        querySnapshot.forEach((doc) => {
            const { stunum, stuname, croom, nroom } = doc.data();
            user.push({
                key: doc.id,
                doc, // DocumentSnapshot
                stunum,
                stuname,
                croom,
                nroom,
            });
        });
        this.setState({
            user
        });
    }
    componentDidMount() {
        this.unsubscribe = this.ref.onSnapshot(this.onCollectionUpdate);
    }

    render() {
        return (
                <div className="container">
                    <div className="panel panel-default">
                        <div className="panel-body">
                            <table className="table table-stripe">
                                <thead>
                                    <tr>
                                        <th>StudentID</th>
                                        <th>Name</th>
                                        <th>Class</th>
                                        <th>Room</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {this.state.user.map(user =>
                                        <tr>
                                            <td>{user.stunum}</td>
                                            <td>{user.stuname}</td>
                                            <td>{user.croom}</td>
                                            <td>{user.nroom}</td>
                                            <td><Link to={`/show/${user.key}`}><button type="button" className="btn btn-outline-warning"></button></Link></td>
                                        </tr>
                                    )}
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
        );
    }
}

export default ViewStu;
