import React, { Component } from "react";
// import ReactDOM from 'react-dom';
import firebase from "../config";
// import { Link } from "react-router-dom";
import DashBoard from "./Dashboard";
import "./Create.css";

class CreateStu extends Component {
  constructor() {
    super();
    this.ref = firebase.firestore().collection("students");
    this.state = {
      stuid: "",
      stunum: "",
      stuname: "",
      croom: "",
      nroom: "",
      cid: "",
    };
  }
  onChange = (e) => {
    const state = this.state;
    state[e.target.name] = e.target.value;
    this.setState(state);
  };

  onSubmit = (e) => {
    e.preventDefault();

    const { stuid, stunum, stuname, croom, nroom, cid } = this.state;

    this.ref
      .add({
        stuid,
        stunum,
        stuname,
        croom,
        nroom,
        cid,
      })
      .then((docRef) => {
        this.setState({
          stuid: "",
          stunum: "",
          stuname: "",
          croom: "",
          nroom: "",
          cid: "",
        });
        this.props.history.push("/");
      })
      .catch((error) => {
        console.error("Error adding document: ", error);
      });
  };

  render() {
    const { stunum, stuname, croom, nroom, cid } = this.state;
    return (
      <>
      <header>
        <DashBoard />
      </header>
      <div className="container text-center">
        <div className="panel panel-default">
          <div className="panel-body ">
            <br /><br />
            <h4 className="text-center">
                Add Student
            </h4>
            <br />
            <form onSubmit={this.onSubmit}>
              <div classNameName="form-group text-center">
                <label for="title">Student ID:</label>
                <br />
                <input
                  type="text"
                  className=" in"
                  name="stunum"
                  value={stunum}
                  onChange={this.onChange}
                  placeholder="Student Id"
                />
              </div>
              <div className="form-group">
                <label for="title">Student Name:</label>
                <br />
                <input
                  type="text"
                  className=" in"
                  name="stuname"
                  value={stuname}
                  onChange={this.onChange}
                  placeholder="Student Name"
                />
              </div>
              <div className="form-group">
                <label for="author">Class ID:</label>
                <br />
                <input
                  type="text"
                  className=" in"
                  name="cid"
                  value={cid}
                  onChange={this.onChange}
                  placeholder="Class"
                />
              </div>
              <div className="form-group">
                <label for="author">Class:</label>
                <br />
                <input
                  type="text"
                  className=" in"
                  name="croom"
                  value={croom}
                  onChange={this.onChange}
                  placeholder="Class"
                />
              </div>
              <div className="form-group">
                <label for="title">Room:</label>
                <br />
                <input
                  type="text"
                  className=" in"
                  name="nroom"
                  value={nroom}
                  onChange={this.onChange}
                  placeholder="Room"
                />
              </div>

            </form>
          </div>
        </div>
        <div className="text-center">
        <br />
              <button type="submit" className="btn btn-success text-center bt">
                Add
              </button>
        </div>
      </div>

      </>
    );
  }
}

export default CreateStu;
